package com.bo.wen.ye.entity;

public class AuToInc {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        int i = 1;
        int j = 2;
        System.out.println(i);
        System.out.println(j + ++i);
        System.out.println(i);
        System.out.println(j + i++);
        System.out.println(i);
        System.out.println(--i);
        System.out.println(i--);
        System.out.println(i);
    }

}
